        // oMergeModel.setProperty("/selectedIdeas", aCBIdeas);
    
        oDialog = this._getIdeaMassMergeDialog();
        oDialog.setModel(oMergeModel, "merge");
        this._prepareListItemForMassIdeaMerge(aFilters);
        
