			getFilterInfoByLink: function(sLink) {
				sLink = sLink || '';
				sLink = sLink.split('?');
				if (sLink.length >= 2) {
					sLink = sLink[1];
				}
				var aFilter = sLink !== '' ? sLink.split('&') : [];
				var oResult = {},
					aFound, sKey, sValue;
				var oRegex = /(.*)=([^&]+)/;
				for (var i = 0; i < aFilter.length; i++) {
					aFound = aFilter[i].match(oRegex);
					if (aFound && aFound.length === 3) {
						sKey = aFound[1].toUpperCase();
						sValue = aFound[2];
						oResult[sKey] = sValue;
					}
				}
				return oResult;
			},

