        this.setProperty("email", sEmail, true);
        // update link control
        oLinkEmail.setText(sEmail);
        oLinkEmail.toggleStyleClass("sapInoWallInvisible", !sEmail);

        var oInputEmail = this._getInputEmail();
        oInputEmail.setValue(sEmail);

